sys_transform_entry.source_script:
answer = (function transformEntry(source) {

    // Add your code here
    var result = '';
    var pattern = '';
    var sourceValue = source.u_description;
    //var pattern = /\\v1\\(.*)/;
    /*
    if ((sourceValue.includes('v1\\data'))) {
        pattern = /\\v1\\(.*)/;
    } else {
        pattern = /\\V1\\(.*)/;
    }
	*/
    if (sourceValue.includes('v1\\data')) {
        pattern = /\\v1\\(.*)/;
    } else if (sourceValue.includes('V1\\data')) {
        pattern = /\\V1\\(.*)/;
    } else if (sourceValue.includes('v1\\apps')) {
        pattern = /\\v1\\(.*)/;
    } else if (sourceValue.includes('V1\\apps')) {
        pattern = /\\V1\\(.*)/;
    }
    //
    // Zeichenfolge nach dem letzten Backslash extrahieren
    var match = sourceValue.match(pattern);
    //
    if (match[0]) {
        //const result = match[0];
        //console.log(result);
        result = match[0].substring(4);
    } else {
        result = '';
    }

    // return ""; // return the value to be put into the target field
    return result;

})(source);
